import { Component } from '@angular/core';
import { district } from 'src/assets/data'
@Component({
  selector: 'app-card',
  templateUrl: './card.component.html',
  styleUrls: ['./card.component.css']
})
export class CardComponent {
  collection = district;


}
<div class="row">
  <div
    class="card"
    *ngFor="let item of collection"
    style="
      background-color: rgb(71, 71, 71);
      width: 18rem;
      height: 300px;
      margin: 20px;
    "
  >
    <img
      style="padding-top: 10px"
      src="{{ item.img }}"
      class="card-img-top"
      alt="..."
    />
    <div class="card-body">
      <h5 class="card-title" style="color: white">{{ item.name }}</h5>
      <div style="width: 100px">
        <p style="font-size: 25px; overflow-y: hidden">{{ item.msg }}</p>
      </div>
      <a href="sub/thiruvananthapuram.html" class="btn btn-primary">visit</a>
    </div>
  </div>
</div>
